//! toDo 추가버튼
const AddBtnHandler = () => {
  if (title.trim() !== '' && cont.trim() !== '') {
    const newTodo = {
      id: uuidv4(),
      title,
      cont,
      isDone: false,
    };
    setToDoList([...toDoList, newTodo]);
    setTitle('');
    setCont('');
  } else {
    alert('제목과 내용을 입력해주세요.');
  }
};

export default AddBtnHandler;
