import React, { useState } from 'react'

function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  const [list, setList] = useState([
    { title: "리액트 공부하기", cont: "리액트 기초를 공부해봅시다.", }
  ]);

  const addBtnHandler = () => {
    console.log(title, cont)
  };

  return (
    <div className='warp'>
      <header>
        <h1>My Todo List</h1>
        <span>React</span>
      </header>
      <div className='input-wrap'>
        <label>
          제목
          <input type='text' 
            value={title}
            onChange={(e)=>{
              setTitle(e.target.value)
            }}
          ></input>
        </label>
        <label>
          내용
          <input type='text' 
            value={cont}
            onChange={(e)=>{
              setCont(e.target.value)
            }}
          ></input>
        </label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='working-area'>

      </div>
    </div>
  )
}

export default App
