import React, { useState } from 'react'

function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  // const [list, setList] = useState([
  //   { title: "리액트 공부하기", cont: "리액트 기초를 공부해봅시다.", }
  // ]);
  const toDoList = [
    { title: "리액트 공부하기", cont: "리액트 기초를 공부해봅시다.", }
  ]

  const addBtnHandler = () => {
    console.log(title, cont)
  };

  return (
    <div className='warp'>
      <header>
        <h1>My Todo List</h1>
        <span>React</span>
      </header>
      <div className='input-wrap'>
        <label>
          제목
          <input type='text' 
            value={title}
            onChange={(e)=>{
              setTitle(e.target.value)
            }}
          ></input>
        </label>
        <label>
          내용
          <input type='text' 
            value={cont}
            onChange={(e)=>{
              setCont(e.target.value)
            }}
          ></input>
        </label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='todoList-wrap'>
        <div className='working'>
          <h2>Working..</h2>
          <ul className='working-list'>
            <li>
              <h3>{toDoList[0].title}기</h3>
              <p>리액트 기초를 공부해봅시다.</p>
              <div className='btn-wrap'>
                <button>삭제하기</button>
                <button>완료</button>
              </div>
            </li>
          </ul>
        </div>
        <div className='done'>
          <h2>Done..!</h2>
          <ul className='done-list'></ul>
        </div>
      </div>

    </div>
  )
}

export default App
