import React, { useState } from 'react';
import Layout from './components/Layout';
import { v4 as uuidv4 } from 'uuid';
import 'normalize.css';
import 'App.css';

function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  const [todoList, setTodoList] = useState()
  const onChangeTitle = (e) => setTitle(e.target.value);
  const onChangeCont = (e) => setCont(e.target.value);

  const toDoList = [
    { title: "리액트 공부하기", cont: "리액트 기초를 공부해봅시다.", }
  ]

  const addBtnHandler = () => {
    console.log(title, cont)
  };

  return (
    <Layout>
      <div className='input-wrap'>
        <label>제목<input type='text' value={title} onChange={onChangeTitle}></input></label>
        <label>내용<input type='text' value={cont} onChange={onChangeCont}></input></label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='todoList-wrap'>
        <div className='working'>
          <h2>Working..</h2>
          <WorkingList toDoList={toDoList} />
        </div>
        <div className='done'>
          <h2>Done..!</h2>
          <ul className='done-list'></ul>
        </div>
      </div>
    </Layout>
  )
}
const WorkingList = (toDoList) => {
  <ul className='working-list'>
    {toDoList.map((item) => {
      return (
        <li key={uuidv4()}>
          <h3>{item.title}기</h3>
          <p>{item.cont}</p>
          <div className='btn-wrap'>
            <button>삭제하기</button>
            <button>완료</button>
          </div>
        </li>);
    })}
  </ul>
}


export default App
