import React, { useState } from 'react';
import Layout from './components/Layout';
import { v4 as uuidv4 } from 'uuid';
import 'normalize.css';
import 'App.css';

function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  const [toDoList, setToDoList] = useState([]);

  const onChangeTitle = (e) => setTitle(e.target.value);
  const onChangeCont = (e) => setCont(e.target.value);

  //! toDo 추가버튼
  const addBtnHandler = () => {
    const newTodo = {
      id: uuidv4(),
      title,
      cont,
    }
    setToDoList([...toDoList, newTodo]);
  };
  //! toDo 삭제버튼
  const removeBtnHandler = (id) => {
    const setNewTodo = toDoList.filter(todo => todo.id !== id);
    setToDoList(setNewTodo);
  }
  //! toDo 완료버튼
  const doneBtnHandler = () => {
  }

  return (
    <Layout>
      <div className='input-wrap'>
        <label>제목<input type='text' value={title} onChange={onChangeTitle}></input></label>
        <label>내용<input type='text' value={cont} onChange={onChangeCont}></input></label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='todoList-wrap'>
        <div className='working'>
          <h2>Working..</h2>
          <ul className='working-list'>
            {toDoList.map((item) => {
              return (
                <li key={item.id}>
                  <h3>{item.title}</h3>
                  <p>{item.cont}</p>
                  <div className='btn-wrap'>
                    <button onClick={()=>removeBtnHandler(item.id)}>삭제하기</button>
                    <button onClick={doneBtnHandler}>완료</button>
                  </div>
                </li>);
            })}
          </ul>
        </div>
        <div className='done'>
          <h2>Done..!</h2>
          <ul className='done-list'>

          </ul>
        </div>
      </div>
    </Layout>
  )
}
const WorkingList = (toDoList) => {
  <ul className='working-list'>
    {toDoList.map((item) => {
      return (
        <li key={item.id}>
          <h3>{item.title}기</h3>
          <p>{item.cont}</p>
          <div className='btn-wrap'>
            <button>삭제하기</button>
            <button>완료</button>
          </div>
        </li>);
    })}
  </ul>
}


export default App
