import React, { useState } from 'react';
import Layout from './components/Layout';
import { v4 as uuidv4 } from 'uuid';
import 'normalize.css';
import 'App.css';

function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  const [toDoList, setToDoList] = useState([]);


  const onChangeTitle = (e) => setTitle(e.target.value);
  const onChangeCont = (e) => setCont(e.target.value);

  //! toDo 추가버튼
  const addBtnHandler = () => {
    const newTodo = {
      id: uuidv4(),
      title,
      cont,
      isDone: false,
    }
    setToDoList([...toDoList, newTodo]);
  };
  //! toDo 삭제버튼
  const removeBtnHandler = (id) => {
    const setNewTodo = toDoList.filter(todo => todo.id !== id);
    setToDoList(setNewTodo);
  }
  //! toDo 완료 취소 버튼
  const toggleDoneHandler = (id) => {
    const updatedList = toDoList.map((todo) =>
      todo.id === id ? { ...todo, isDone: !todo.isDone } : todo
    );

    setToDoList(updatedList);
  };



  return (
    <Layout>
      <div className='input-wrap'>
        <label>제목<input type='text' value={title} onChange={onChangeTitle}></input></label>
        <label>내용<input type='text' value={cont} onChange={onChangeCont}></input></label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='todoList-wrap'>
        <div className='working'>
          <h2>Working..</h2>
          <ul className='working-list'>
            {toDoList
              .filter((item) => !item.isDone)
              .map((item) => (
                <Todo
                  key={item.id}
                  item={item}
                  removeBtn={removeBtnHandler}
                  toggleDone={toggleDoneHandler}
                />
              ))}
          </ul>
        </div>
        <div className='done'>
          <h2>Done..!</h2>
          <ul className='working-list'>
            {toDoList
              .filter((item) => item.isDone)
              .map((item) => (
                <Todo
                  key={item.id}
                  item={item}
                  removeBtn={removeBtnHandler}
                  toggleDone={toggleDoneHandler}
                />
              ))}
          </ul>
        </div>
      </div>
    </Layout>
  )
}
const Todo = ({item, removeBtn, toggleDone}) => {
  return (
    <li key={item.id} className={item.isDone ? 'true' : ''}>
      <h3>{item.title}</h3>
      <p>{item.cont}</p>
      <div className='btn-wrap'>
        <button onClick={()=>removeBtn(item.id)}>삭제하기</button>
        <button onClick={()=>toggleDone(item.id)}>{item.isDone ? '취소' : '완료'}</button>
      </div>
    </li>
    );
}

export default App
