import React, { useState } from 'react';
import GlobalStyle from 'GlobalStyles';
import styled from 'styled-components';
import Layout from './components/Layout';
import Todo from 'components/Todo';
import { v4 as uuidv4 } from 'uuid';
import 'normalize.css';
import 'App.css';


function App() {

  const [title, setTitle] = useState("");
  const [cont, setCont] = useState("");
  const [toDoList, setToDoList] = useState([]);

  const onChangeTitle = (e) => setTitle(e.target.value);
  const onChangeCont = (e) => setCont(e.target.value);

  //! toDo 추가버튼
  const addBtnHandler = () => {
    const newTodo = {
      id: uuidv4(),
      title,
      cont,
      isDone: false,
    }
    setToDoList([...toDoList, newTodo]);
  };
  //! toDo 삭제버튼
  const removeBtnHandler = (id) => {
    const setNewTodo = toDoList.filter(todo => todo.id !== id);
    setToDoList(setNewTodo);
  }
  //! toDo 완료 취소 버튼
  const toggleDoneHandler = (id) => {
    const updatedList = toDoList.map((todo) =>
      todo.id === id ? { ...todo, isDone: !todo.isDone } : todo
    );
    setToDoList(updatedList);
  };

  return (
    <Layout>
      <GlobalStyle />
      <div className='input-wrap'>
        <label>제목<input type='text' placeholder='제목을 입력하세요' value={title} onChange={onChangeTitle}></input></label>
        <label>내용<input type='text' placeholder='내용을 입력하세요.' value={cont} onChange={onChangeCont}></input></label>
        <button onClick={addBtnHandler}>추가하기</button>
      </div>
      <div className='todoList-wrap'>
        <div className='working'>
          <h2>Working..</h2>
          <ul className='working-list'>
            {toDoList
              .filter((item) => !item.isDone)
              .map((item) => (
                <Todo
                  key={item.id}
                  item={item}
                  removeBtn={removeBtnHandler}
                  toggleDone={toggleDoneHandler}
                />
              ))}
          </ul>
        </div>
        <div className='done'>
          <h2>Done..!</h2>
          <ul className='working-list'>
            {toDoList
              .filter((item) => item.isDone)
              .map((item) => (
                <Todo
                  key={item.id}
                  item={item}
                  removeBtn={removeBtnHandler}
                  toggleDone={toggleDoneHandler}
                />
              ))}
          </ul>
        </div>
      </div>
    </Layout>
  )
}
export default App
